import React from 'react';
import { useHistory } from 'react-router-dom';

const ProductCard = ({ product, addToCart }) => {
  const history = useHistory();

  const handleCardClick = () => {
    history.push(`/product/${product.id}`); // Переход на страницу товара
  };

  return (
    <div className="product-card" onClick={handleCardClick}>
      <img src={product.image} alt={product.name} />
      <h2>{product.name}</h2>
      <p className="price">{product.price.toLocaleString()} ₽</p> {/* Форматирование цены */}
      <button onClick={(e) => {
        e.stopPropagation(); // Остановка события, чтобы не вызывать переход на страницу товара
        addToCart(product);
      }}>Добавить в корзину</button>
    </div>
  );
};

export default ProductCard;
