import React, { useState } from 'react';
import './Cart.css';

const Cart = ({ cartItems, removeFromCart }) => {
  const [isOpen, setIsOpen] = useState(false); // Состояние для открытия/закрытия корзины

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  const handleQuantityChange = (id, increment) => {
    const updatedItems = cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = Math.max(1, item.quantity + increment);
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    // Здесь должен быть вызов функции, которая обновляет состояние корзины на уровне родительского компонента
    // Например, updateCart(updatedItems);
  };

  return (
    <div>
      {/* Кнопка открытия/закрытия корзины */}
      <div className="cart" onClick={toggleCart}>
        <p>Корзина:</p>
        <span>{cartItems.length} товаров</span>
      </div>

      {/* Содержимое корзины (отображается при открытии) */}
      {isOpen && (
        <div className="cart-details">
          <h3>Содержимое корзины</h3>
          {cartItems.length === 0 ? (
            <p>Корзина пуста</p>
          ) : (
            <ul>
              {cartItems.map((item, index) => (
                <li key={index}>
                  <img src={item.image} alt={item.name} width="50" />
                  {item.name} — {item.price} ₽, размер: {item.size}
                  <div className="quantity-controls">
                    <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                  </div>
                  <button onClick={() => removeFromCart(item.id)}>Удалить</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default Cart;
