import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import loadData from './googleSheets';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import ProductDetail from './components/ProductDetail';
import LanguageSwitcher from './components/LanguageSwitcher';
import RecommendedProducts from './components/RecommendedProducts';
import DeliveryOptions from './components/DeliveryOptions';
import OrderTracking from './components/OrderTracking';
import UserProfile from './components/UserProfile';
import ChangePassword from './components/ChangePassword';
import Wishlist from './components/Wishlist';
import Reviews from './components/Reviews';
import StoreInfo from './components/StoreInfo';
import PromoCode from './components/PromoCode';
import './App.css';

const App = () => {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Все');
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [language, setLanguage] = useState('ru');
  const [orderHistory, setOrderHistory] = useState([]);
  const [userProfile, setUserProfile] = useState({ name: '', email: '' });
  const [wishlist, setWishlist] = useState([]);
  const [discount, setDiscount] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const data = await loadData();
      setProducts(data);
    };
    fetchData();
  }, []);

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Все' || product.category === selectedCategory;
    const matchesSearchTerm = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearchTerm;
  });

  const addToCart = (product) => {
    const existingProduct = cartItems.find(item => item.id === product.id);
    if (existingProduct) {
      setCartItems(cartItems.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleOrder = () => {
    const newOrder = {
      id: orderHistory.length + 1,
      items: cartItems,
      date: new Date().toLocaleDateString(),
      totalPrice: cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0) * (1 - discount),
    };
    setOrderHistory([...orderHistory, newOrder]);
    setCartItems([]);
  };

  const updateUserProfile = (profileData) => {
    setUserProfile(profileData);
  };

  const addToWishlist = (product) => {
    if (!wishlist.find(item => item.id === product.id)) {
      setWishlist([...wishlist, product]);
    }
  };

  const handlePromoCode = (code) => {
    if (code === 'SUMMER20') {
      setDiscount(0.2);
      alert('Промокод применен! Скидка 20%!');
    } else {
      alert('Неверный промокод.');
    }
  };

  return (
    <Router>
      <div className="App">
        <h1 style={{ textAlign: 'center' }}>Магазин одежды и обуви</h1>

        <LanguageSwitcher language={language} setLanguage={setLanguage} />

        <PromoCode onApply={handlePromoCode} />

        <div className="search-container">
          <input
            type="text"
            className="search-input"
            placeholder="Поиск по товарам..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="category-buttons">
          <button onClick={() => setSelectedCategory('Все')}>Все</button>
          <button onClick={() => setSelectedCategory('Одежда')}>Одежда</button>
          <button onClick={() => setSelectedCategory('Обувь')}>Обувь</button>
        </div>

        <div className="container">
          {filteredProducts.map(product => (
            <Link key={product.id} to={`/product/${product.id}`}>
              <ProductCard product={product} addToCart={addToCart} addToWishlist={addToWishlist} />
            </Link>
          ))}
        </div>

        <Cart cartItems={cartItems} removeFromCart={removeFromCart} onOrder={handleOrder} />
        <DeliveryOptions />
        <h2>Рекомендуемые товары</h2>
        <RecommendedProducts products={products} />
        <h2>История заказов</h2>
        <OrderTracking orderHistory={orderHistory} />
        
        <Link to="/profile">Профиль пользователя</Link>
        <Link to="/change-password">Изменить пароль</Link>
        <Link to="/wishlist">Список желаемого</Link>
        <Link to="/reviews">Отзывы</Link>
        <Link to="/store-info">Информация о магазине</Link>

        <Routes>
          <Route path="/product/:id" element={<ProductDetail products={products} />} />
          <Route path="/profile" element={<UserProfile userProfile={userProfile} updateUserProfile={updateUserProfile} />} />
          <Route path="/change-password" element={<ChangePassword />} />
          <Route path="/wishlist" element={<Wishlist wishlist={wishlist} />} />
          <Route path="/reviews" element={<Reviews />} />
          <Route path="/store-info" element={<StoreInfo />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
