import React from 'react';

const RecommendedProducts = ({ products }) => {
  return (
    <div className="recommended-products">
      {products.slice(0, 3).map(product => (
        <div key={product.id}>
          <h4>{product.name}</h4>
        </div>
      ))}
    </div>
  );
};

export default RecommendedProducts;
