import React, { useState } from 'react';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ProductDetail from './components/ProductDetail';

const App = () => {
  const [selectedCategory, setSelectedCategory] = useState('Все'); // Состояние для выбранной категории
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState(''); // Состояние для поиска

  const products = [
    { id: 1, name: 'Футболка', price: 1500, size: 'M', image: `${process.env.PUBLIC_URL}/img/shirt.jpg`, category: 'Одежда' },
    { id: 2, name: 'Кроссовки', price: 5000, size: '42', image: `${process.env.PUBLIC_URL}/img/sneakers.jpg`, category: 'Обувь' },
    { id: 3, name: 'Кроссовки 2', price: 10000, size: '44', image: `${process.env.PUBLIC_URL}/img/sneaker.jpg`, category: 'Обувь' },
  ];

  // Фильтрация товаров по категории и поисковому запросу
  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Все' || product.category === selectedCategory;
    const matchesSearchTerm = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearchTerm;
  });

const addToCart = (product) => {
  const existingProduct = cartItems.find(item => item.id === product.id);
  if (existingProduct) {
    setCartItems(cartItems.map(item => 
      item.id === product.id 
      ? { ...item, quantity: item.quantity + 1 } 
      : item
    ));
  } else {
    setCartItems([...cartItems, { ...product, quantity: 1 }]); // Добавьте новое свойство quantity
  }
};

const Cart = ({ cartItems, removeFromCart }) => {
  return (
    <div className="cart">
      <h2>Корзина</h2>
      {cartItems.map(item => (
        <div key={item.id}>
          <span>{item.name} - {item.quantity} шт.</span>
          <button onClick={() => removeFromCart(item.id)}>Удалить</button>
        </div>
      ))}
    </div>
  );
};


  const removeFromCart = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  return (
    <div className="App">
      <h1>Магазин одежды и обуви</h1>

      {/* Поле ввода для поиска */}
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Поиск по товарам..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Кнопки для выбора категорий */}
      <div className="category-buttons">
        <button onClick={() => setSelectedCategory('Все')}>Все</button>
        <button onClick={() => setSelectedCategory('Одежда')}>Одежда</button>
        <button onClick={() => setSelectedCategory('Обувь')}>Обувь</button>
      </div>

      {/* Отображение товаров по выбранной категории и поисковому запросу */}
      <div className="container">
        {filteredProducts.map(product => (
          <ProductCard key={product.id} product={product} addToCart={addToCart} />
        ))}
      </div>

      {/* Компонент корзины */}
      <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
    </div>
  );
};

export default App;
