import React from 'react';
import './StoreInfo.css';

const StoreInfo = () => {
  return (
    <div className="store-info">
      <h2>Информация о магазине</h2>
      <p>Мы предлагаем лучшую одежду и обувь для любых нужд. Наши товары высокого качества и по конкурентным ценам. Удовлетворение потребностей клиентов — наш главный приоритет.</p>
    </div>
  );
};

export default StoreInfo;
