import { GoogleSpreadsheet } from 'google-spreadsheet';

// Замените на ваши данные
const SPREADSHEET_ID = '1xxce_ZNRabWd5DnE6spkAL-e8gCBR7lydLQi6JjZwik';
const CLIENT_EMAIL = 'al-573@database-436521.iam.gserviceaccount.com';
const PRIVATE_KEY = 'nMIIEvAIBADANBgkqhkiG9w0BAQEFAASCBKYwggSiAgEAAoIBAQDMypslPpPzRohL';

const doc = new GoogleSpreadsheet(SPREADSHEET_ID);

const loadData = async () => {
  try {
    await doc.useServiceAccountAuth({
      client_email: CLIENT_EMAIL,
      private_key: PRIVATE_KEY.replace(/\\n/g, '\n'),
    });
    await doc.loadInfo();
    const sheet = doc.sheetsByIndex[0]; // Замените на нужный вам лист
    const rows = await sheet.getRows();
    
    return rows.map(row => ({
      id: row.id,
      name: row.name,
      price: parseFloat(row.price),
      category: row.category,
      image: row.image,
      description: row.description,
    }));
  } catch (error) {
    console.error('Ошибка загрузки данных:', error);
    return [];
  }
};

export default loadData;
