import React from 'react';

const Cart = ({ cartItems, removeFromCart, onOrder }) => {
  return (
    <div className="cart">
      <h2>Корзина</h2>
      {cartItems.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <ul>
          {cartItems.map(item => (
            <li key={item.id}>
              {item.name} - {item.quantity} шт.
              <button onClick={() => removeFromCart(item.id)}>Удалить</button>
            </li>
          ))}
        </ul>
      )}
      <button onClick={onOrder}>Оформить заказ</button>
    </div>
  );
};

export default Cart;
