import React from 'react';
import './ProductCard.css'; // Импорт стилей для компонента

const ProductCard = ({ product, addToCart, addToWishlist }) => {
  return (
    <div className="product-card">
      <img src={product.image} alt={product.name} className="product-image" />
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Цена: {product.price} ₽</p>
      <div className="product-actions">
        <button onClick={() => addToCart(product)} className="add-to-cart">Добавить в корзину</button>
        <button onClick={() => addToWishlist(product)} className="add-to-wishlist">Добавить в избранное</button>
      </div>
    </div>
  );
};

export default ProductCard;
